function out=putinfields(in)% puts output of track.m into a field format for easier analysis% out=putinfields(in, clean)% 4/6/05, damon clark, daclark@fas.harvard.eduTOOSHORT=5;   % tracks of fewer than this not countedMAXGAP=3;     % gaps of more than this are split into two tracksTOTMOV=3;     % total radius to move out of during video in order to be counted[u dum locs]=unique(in(:,4));for i=1:length(u)    choose=find(locs==i);    out(i).x=in(choose,1);    out(i).y=in(choose,2);    out(i).f=in(choose,3);    out(i).num=length(choose);end